package cn.loli.client.utils;import cn.loli.client.utils.render.AnimationUtil;import cn.loli.client.utils.render.AnimationUtils;public class Translate {	private float x;	private float y;	public Translate(float x, float y) {		this.x = x;		this.y = y;	}	public void translate(float targetX, float targetY) {		x = (float) AnimationUtil.Anim(x, targetX, 1.5);		y = (float) AnimationUtil.Anim(y, targetY, 1.5);	}	public void translate(float targetX, float targetY , double speed) {		x = (float) AnimationUtil.Anim(x, targetX, speed);		y = (float) AnimationUtil.Anim(y, targetY, speed);	}	public float getX() {		return this.x;	}	public void setX(float x) {		this.x = x;	}	public float getY() {		return this.y;	}	public void setY(float y) {		this.y = y;	}}