package cn.loli.client.utils.render;import net.minecraft.client.Minecraft;import java.math.BigDecimal;import java.math.MathContext;public class AnimationUtil {	public static double Anim(double now, double desired, double speed) {		double dif = Math.abs(now - desired);		int fps = Minecraft.getDebugFPS();		if (dif > 0.0D) {			double animationSpeed = roundToDecimalPlace(Math.min(10.0D, Math.max(0.05D, 144.0D / fps * dif / 10.0D * speed)), 0.05D);			if (dif != 0.0D && dif < animationSpeed) animationSpeed = dif;			if (now < desired) return now + animationSpeed;			if (now > desired) return now - animationSpeed;		}		return now;	}	public static double roundToDecimalPlace(double value, double inc) {		double halfOfInc = inc / 2.0D;		double floored = StrictMath.floor(value / inc) * inc;		if (value >= floored + halfOfInc)			return (new BigDecimal(StrictMath.ceil(value / inc) * inc, MathContext.DECIMAL64)).stripTrailingZeros().doubleValue();		return (new BigDecimal(floored, MathContext.DECIMAL64)).stripTrailingZeros().doubleValue();	}}